import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Layout from './components/Layout';
import WorkspacePage from './pages/WorkspacePage';
import ConversationListPage from './pages/ConversationListPage';
import SettingsPage from './pages/SettingsPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import ChangePasswordPage from './pages/ChangePasswordPage';
import HelpPage from './pages/HelpPage'; // <-- استيراد المكون الجديد
import { AuthProvider, useAuth } from './components/AuthContext';
import './App.scss';
// تم حذف استيراد `help-page.scss` من هنا لأنه سيُستورد داخل المكون نفسه

// PrivateRoute component to protect routes
const PrivateRoute = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();
  if (loading) {
    return <div>Loading...</div>; // Or a proper spinner component
  }

  return isAuthenticated ? children : <Navigate to="/login" replace />;
};

function App() {
  return (
    <Router>
      <AuthProvider>
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />

          <Route path="/" element={<PrivateRoute><Layout /></PrivateRoute>}>
            <Route index element={<ConversationListPage />} />
            <Route path="conversation/:id" element={<WorkspacePage />} />
            <Route path="settings" element={<SettingsPage />} /> 
            <Route path="help" element={<HelpPage />} /> {/* <-- استخدام المكون الجديد هنا */}
            <Route path="change-password" element={<ChangePasswordPage />} />
          </Route>

          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </AuthProvider>
    </Router>
  );
}

export default App;
